insert into music(mseq, abseq, atseq, theme, chart, gseq, title, content, titleyn, musicby, lyricsby, producingby, src) values(music_seq.nextval, 1, 1, '', '1|2', 1, '넌 감동이었어', '미련하게 아무도 모를것 같아<br>태연한척 지내왔어 너 떠나버린뒤<br>다 알았대 어설픈 나의 눈빛은<br>행복했던 지난날의 나와 너무 달라서<br>이별했음을 느낄수 밖에<br>너와 나 정말 그때는 좋았었나봐<br>나 화낼줄도 몰라 내내 즐거웠대<br>그래 그랬었지 널 사랑하기에<br>세상은 나에게 커다란 감동이었어<br>그 순간을 잃는다면 내가 살아온 짧은 세월은<br>너무나 보잘것없어<br>되돌려 보려해 너를 찾으려해<br>너 없이 살아도 멀쩡히 숨은 쉬겠지만<br>후회와 그리움만으로는 견딜 수 없어<br>하루도 자신이 없어<br>(간주중)<br>초라했대 어설픈 나의 눈빛은<br>행복했던 지난날의 나와 너무달라서<br>이별 했음을 느낄 수 밖에<br>너와 나 헤어 질 줄은 아무도 몰랐대<br>하루가 너무 짧던 우리의 날들이<br>그래 그랬었지 널 사랑하기에<br>세상은 나에게 커다란 감동이었어<br>그 순간을 잃는다면 내가 살아온<br>짧은 세월을 너무나 보잘것 없어<br>되돌려 보려해 너를 찾으려해<br>너없이 살아도 멀쩡히 숨은 쉬겠지만<br>후회와 그리움 만으로는 견딜 수 없어<br>하루도 자신이 없어<br>도저히<br>', 'Y', '', '', '', 'https://docs.google.com/uc?export=open&id=1un0vkktibB0MGmbObxM4YuWU79HYYlrb');
insert into music(mseq, abseq, atseq, theme, chart, gseq, title, content, titleyn, musicby, lyricsby, producingby, src) values(music_seq.nextval, 1, 1, '', '1|2', 1, '선인장', '눈 뜨지 말아요<br>잠든 채 그냥 있어요<br>그대 눈물 못 본 척 할게요<br>한번만 더 그대의 고운 얼굴을<br>가슴에 새길 수 있게 해줘요<br>날 잡지 말아요<br>내 못난 사랑 안에서<br>그댄 너무 힘들었잖아요<br>내 욕심이 그대를 깨울 수 없게<br>이대로 말없이<br>떠날 수 있게 도와줘요<br>사랑해서 내가 가까이 갈수록<br>그대 맘엔 상처가 깊어져<br>우리는 너무나 닮지 못했죠<br>우리에게 허락된 건<br>그저 바라보는 일<br>거짓을 말할 땐<br>내 눈을 피하던 그댈<br>내가 너무나 잘 알잖아요<br>변함없이 사랑한다는 거짓말<br>오히려 내 맘을<br>병들게 했죠 알잖아요<br>사랑해서 내가 가까이 갈수록<br>그대 맘엔 상처가 깊어져<br>우리는 너무나 닮지 못했죠<br>우리에게 허락된 건<br>그저 바라보는 일<br>혼자 가야 할 그대의 길이<br>힘이 들 땐 나의 이름을 불러요<br>안아줄 수는 없지만<br>그댈 지켜줄 수 있도록<br>가끔씩만 날 찾아줄 순 없나요<br>다른 사람 함께라도 좋아요<br>그대의 모습만 볼 수 있다면<br>괜찮아요 얼마든지<br>견뎌낼 수 있어요<br>나 기다릴게요', 'N', '', '', '', 'https://docs.google.com/uc?export=open&id=1W8EI4BzCb0FRcScO-6j_0jaPjBGopC0D');
insert into music(mseq, abseq, atseq, theme, chart, gseq, title, content, titleyn, musicby, lyricsby, producingby, src) values(music_seq.nextval, 1, 1, '', '1|2', 1, '사랑해서 슬픈 날', '무슨 말을 해야 하는지 자꾸만<br>흐르는 눈물 또 참아 내려고<br>그대 사랑 앞에서 괜한 웃음<br>자꾸 보인 건 아닌지<br>너무나 행복해 보여요 그동안<br>나의 곁에선 본적 없던 그대죠<br>너무 쉽게 웃음을 줄 수 있는<br>그대 곁에 사랑이 난 부러워져요<br>술에 취한 척 기대던 떨림도<br>그대 모르게 만들었던 우연도<br>내겐 너무 큰 행복이었나요<br>내겐 그것조차 줄 수 없나요<br>사랑한 순간들을 감춰왔듯이<br>웃으며 보내줘야 하겠죠<br>그대 행복한 모습<br>너무나 바라던 일인데 눈물뿐이죠<br>좋은 사람이길 바래요 눈부신<br>그대 모습을 지켜 줄 수 있도록<br>나와 닮은 슬픔에<br>두 번 다시 그대 울지 않도록<br>나 기도 할게요<br>그대 전화에 잠 못 들던 밤도<br>그대 웃음에 밝아지던 세상도<br>내겐 너무 큰 행복이었나요<br>내겐 그것조차 줄 수 없나요<br>사랑한 순간들을 감춰왔듯이<br>웃으며 보내줘야 하겠죠<br>그대 행복한 모습<br>너무나 바라던 일인데<br>무엇도 달라지는 건 없는거죠<br>영원히 그댈 볼 수 있겠죠<br>항상 그래 왔듯이<br>또 다시 기다릴 수 있죠<br>눈물뿐인 날<br>그대는 모르게<br>', 'N', '', '', '', 'https://docs.google.com/uc?export=open&id=18wffeFrkLiP6LTmfDXMPEg4UQLiKZ1hS');
insert into music(mseq, abseq, atseq, theme, chart, gseq, title, content, titleyn, musicby, lyricsby, producingby, src) values(music_seq.nextval, 1, 1, '', '1|2', 1, '좋을텐데', '좋을텐데 너의 손 꼭 잡고 그냥<br>이 길을 걸었으면 내겐 너뿐인 걸<br>니가 알았으면 좋을텐데 우<br>얼마 전 만난 남자 얘기<br>오늘만 해도 벌써 몇 번째니<br>그에게 전화가 또 왔었다며<br>조금은 얼굴을 붉히는 너 <br>집에 돌아오는 길은<br>포근한 달빛마저 슬퍼 보여<br>마음이 아파 <br>나를 위로해줄 니가 있다면<br>좋을텐데 너의 손 꼭 잡고 그냥<br>이 길을 걸었으면 내겐 너뿐인 걸<br>니가 알았으면 좋을텐데<br>괜찮은 사람인지 아닌지<br>한번 본 적도 없는 내가 알아<br>그에게 선물을 또 받았다며<br>유난히 행복해 보이는 너 <br>집에 돌아오는 길은<br>포근한 달빛마저 슬퍼 보여<br>마음이 아파 나를 위로해줄<br>니가 있다면 좋을텐데<br>나 아닌 사람 얘기에 때론<br>화도 내어 봤으면 그저 너의 곁에<br>내가 있었으면 좋을텐데<br>모르겠니 애써 지켜온<br>우정마저 놓칠 것만 같아<br>기다리던 또 망설이던 <br>그런 나 이젠 이젠<br>좋을텐데 너의 손 꼭 잡고 그냥<br>이 길을 걸었으면 내겐 너뿐인 걸<br>니가 알았으면 좋을텐데<br>나 아닌 사람 얘기에 때론<br>화도 내어봤으면 그저<br>너의 곁에 내가 있었으면<br>좋을텐데 너의 손 꼭 잡고 그냥<br>이 길을 걸었으면 너뿐인 걸<br>니가 알았으면 좋을텐데<br>나 아닌 사람 얘기에 때론<br>화도 내어봤으면<br>', 'N', '', '', '', 'https://docs.google.com/uc?export=open&id=1w4ZDnej4E3qhAWC9OJm6oGjs3L9muwT1');
insert into music(mseq, abseq, atseq, theme, chart, gseq, title, content, titleyn, musicby, lyricsby, producingby, src) values(music_seq.nextval, 1, 1, '', '1|2', 1, 'Love Letter', 'I love my little baby<br>항상 보고싶은 넌<br>My world I love my pretty baby<br>모든걸 주고싶어<br>말로 하려했지만 도무지 자신없어<br>부족한 글이지만 <br>내맘이 네게 갈수 있다면<br>달콤한 말 사랑의 말 서툰나지만<br>널 향한 마음만은 정말 자신 있는걸<br>눈을뜨면 늘 그곳에 하늘이 있듯<br>말없이 네 곁을 지키는<br>하늘이 되줄게<br>I love my little girl<br>항상 보고싶은 넌<br>My world I love my pretty baby<br>모든걸 주고싶어<br>한참을 고민해도 아무리 애를써도<br>너의 앞에만 서면 겨울처럼 얼어붙는 난<br>달콤한 말 사랑의 말 서툰나지만<br>널 위한 마음만은<br>정말 자신있는걸<br>눈을뜨면 늘 하늘이 우릴 비추듯<br>영원히 너만을 지키는 하늘이 되줄게<br>달콤한말 사랑의말 서툰나지만 <br>널 향한 마음만은<br>자신있는 나란걸<br>눈을뜨면 늘 그곳에 하늘이 있듯<br>말없이 네 곁을 지키는<br>하늘이 되줄게<br>', 'Y', '', '', '', 'https://docs.google.com/uc?export=open&id=1nW-qZNPa-tvOq1hSDJ_PryGJcwjE8QMD');
