insert into music(mseq, abseq, atseq, theme, chart, gseq, title, content, titleyn, musicby, lyricsby, producingby, src) values(music_seq.nextval, 60, 14, '', '1|2', 1, '편지', '좀 늦어졌지<br>한 동안 참 바쁘게 지냈거든<br>물론 잠시라도 너의 모습<br>잊어버린 적은 없지만<br>넌 어떠니 이제는 좀 익숙해졌니<br>그렇게도 가고 싶어했던<br>그 곳은 널 반겨주고있겠지<br>깨알같은 글씨에<br>아련히 남아있는 너의 향기<br>네가 사는 먼 곳의 바람,<br>그 거리와 사람들<br>날이 갈수록 조금씩 우린 게을러지고<br>하고 싶은 이야기도 천천히 줄어 가겠지만<br>넌 어떠니<br>그래도 날 믿어 주겠니<br>혹시 네가 나를 잊어버린다 해도<br>널 잊지는 않는다고<br>너를 그리워하는<br>낯익은 얼굴들과 그 숨결을<br>내가 있는 이 곳의 바람,<br>이 거리의 향기를<br>전해 줄 수 있을까 이 짧은 편지가<br>다시 만날 그 날을 기다리고 있다고<br>너를 그리워하는 낯익은 얼굴들과<br>그 숨결을 게으른 내 편지가,<br>너에게 전해줄 수 있기를.<br>', 'N', '윤상', '박창학', '윤상', 'https://docs.google.com/uc?export=open&id=13X0dtejkK1GRHEu1V0IqBnSeoazq2ymG');
insert into music(mseq, abseq, atseq, theme, chart, gseq, title, content, titleyn, musicby, lyricsby, producingby, src) values(music_seq.nextval, 60, 14, '', '1|2', 1, '내가 사랑한 사람', '너, 나를 모른척해.<br>잠시 알고 지내던 그저 친구라 말해.<br>전에, 본것도 같다고,<br>그런 것 같아서 기억나질 않는다고..<br>늦기전에, 어서 그에게로 가<br>혹시, 알아버릴까 내가 불안하잖아.<br>그래, 난 이걸로 됐어 행복한 니모습<br>그걸로 난 충분한걸...<br>돌아가, 어서 그를 안아줘...<br>많이 서 있잖니 네게 오라면서<br>괜찮아, 이미 모든 걸 잃은 나에겐<br>그 모습이라도 내겐 소중한걸 믿을게...<br>나, 네게 잘한 것 같아.<br>마치 남인것처럼 그가 생각하잖아.<br>허나, 그것도 모자라 잊어야 한다면<br>미안해, 그럴순 없어...<br>후회돼, 다시 네게 돌아가<br>그에게 말하고 싶어...사랑한 사람 나라고...<br>죽어도 넌 아닐 꺼 라고...<br>돌아가, 어서 그를 안아줘...<br>많이 서 있잖니 네게 오라하면서<br>괜찮아, 이미 모든 걸 잃은 나에겐<br>그 모습이라도 내겐 소중한걸 믿을게...<br>', 'N', '조우진', '조우진', '', 'https://docs.google.com/uc?export=open&id=10MsLUI52JIykNmnkpbphAI6Muxp8ymly');
