insert into music(mseq, abseq, atseq, theme, chart, gseq, title, content, titleyn, musicby, lyricsby, producingby, src) values(music_seq.nextval, 68, 15, '', '1|2', 1, '나의 너에게', '(언제나 그대는 나에게) 왠지 가벼운 시선조차<br>너에겐 향하기가 난 힘들어 자연스럽게 보이려<br>애쓰지만 오히려 어색해질 뿐이야 내가 살아가는<br>동안에 다시 또 이런 날이 올 줄이야 아주 오래<br>전에 잃어버린 설레임 난 다시 어린 아이처럼<br>이런 나에게 나에게 너는 아무 것도 바라지<br>않아도 돼 너에게 나의 너에게 이미 모든 것을<br>다 주려고 하는 나니까 아주 자그만 너의<br>목소리조차 나는 다 들을 수 있어 그렇게 하지<br>않으려 애쓰지만 벌써 너를 바라보고 있어 내가<br>살아가는 동안에 다시 또 이런 내가 될 줄이야<br>아주 오래 전에 잃어버린 내 모습 난 다시 어린<br>아이처럼 이런 나에게 나에게 너는 아무 것도<br>바라지 않아도 돼 너에게 나의 너에게 이미 모든<br>것을 다 주려고 하는 나니까 나는 네게 되고 싶어<br>아낌없이 주는 나무 너만 보고 살고픈 사랑에<br>아주 많이 아무 것도 바라지 않아 너와 함께라면<br>다 주겠어 내 모든 것 모두 내 전부 다 바쳐 세상<br>그 누구보다 행복하게 해주고 싶어 yo my love<br>친구들은 말하지 이런 나의 모습에 걱정이<br>된다고 하지만 나는 괜찮아 세상 어떤 그<br>누구보다도 행복할 뿐야 언제나 그리는 얼굴 하나<br>너 뿐이라는 걸 너와 함께면 나는 어디라도 좋아<br>그런 맘뿐야 나에게 이런 나에게 너는 아무 것도<br>바라지 않아도 돼 너에게 나의 너에게 이미 모든<br>것을 다 주려고 하는 나니까 (이런 나에게 나에게<br>너는 아무 것도 바라지 않아도 돼 너에게 나의<br>너에게) 모든 것을 다 주려고 하는 나니까<br>', 'N', '', '', '', 'https://docs.google.com/uc?export=open&id=1BZoKw8LDqjQyQ6h58buNPQ9FeNKLPLdk');
insert into music(mseq, abseq, atseq, theme, chart, gseq, title, content, titleyn, musicby, lyricsby, producingby, src) values(music_seq.nextval, 68, 15, '', '1|2', 1, 'Be Mine', '다시 날 사랑한단 그 말이 그 속에 숨겨놓은<br>너의 거짓이 이젠 보지 않으려 애를 써 봐도<br>Always be mine 아픈 듯 내게 눈물 보이고<br>뒤돌아 서면 쉽게 웃어버리고 잠시 외롭던 니<br>맘 채우면 또 언제든지 나를 떠날 너잖아<br>그런 너마저 사랑할 사람 항상 받아줄 사람<br>더는 없어 바보같던 날 이젠 기대하지마<br>(더이상 나를 찾지마) 이별을 해 느꺼야만 하는<br>아픔이라면 네게서 이미 충분하게 받아온 나야<br>이젠 어떤 사랑도 다른 누구라도 네게 없기를<br>난 바랄 뿐이야 한때라도 너 역시 날 사랑했다면<br>이대로 다신 나를 찾지마 아름다웠던 지난<br>기억마저 너의 거짓으로 물들기전에<br>네겐 나뿐이라고 말해도 또 예전처럼 나의 품에<br>안겨도 잠시 아쉬운 니 맘 끝나면 또 언제든지<br>나를 떠날 너잖아 그런 너마저 사랑할 사람 항상<br>받아줄 사람 더는 없어 바보같던 날 이젠<br>기대하지마 (더이상 나를 찾지마)<br>이별을 해 느껴야만 하는 아픔이라면 네게서<br>이미 충분하게 받아온 나야 이젠 어떤 사랑도<br>다른 누구라도 네게 없기를 난 바랄 뿐이야<br>한때라도 너 역시 날 사랑했다면 이대로 다신<br>나를 찾지마 아름다웠던 지난 기억마저<br>너의 거짓으로 물들기전에 자꾸만 다시 안고<br>싶어져 또 다시 널 잡기전에 떠나줘<br>날떠나줘<br>', 'N', 'Lee Yong Min', '', '', 'https://docs.google.com/uc?export=open&id=13Ja2A-ePZpsZNiRdutJNysl-2yzbbYJ_');
insert into music(mseq, abseq, atseq, theme, chart, gseq, title, content, titleyn, musicby, lyricsby, producingby, src) values(music_seq.nextval, 68, 15, '', '1|2', 1, '거짓말', '아니라고..꿈일거라고..이럴 순 없다고...<br>지금까지 몇번을 말했는지...<br>외투를 입고 일어서고 돌아서서<br>멀어지던 모습이 아직 믿어지지 않아...<br>하루라도 일분이라도 일초라도<br>넌 나를 정말 사랑했었는지...<br>셀수도 없이 사랑한단 얘길 했던<br>너도 나를 떠났어...<br>이 세상이 전부 거짓말 같아...<br>언젠가 누군갈 만난다면..사랑이란걸 한다면..<br>(또 언젠가 다시 누굴 만나 사랑하면..)<br>생각 하겠지...날 떠나가는 일이 너무 간단했었던 널...<br>너는 내게 행복하라고 말했지만<br>나는 니가 아프길 바랬어....<br>내가 아프면 슬퍼할까?<br>아니...내가 행복하면 슬플까?<br>어떡해야 니가 벌을 받을까?<br>언젠가 누군갈 만난다면...사랑이란걸 한다면....<br>생각 하겠지 날 떠나가는 일이 너무 간단했었던 널...<br>술을 마셔도 흐느껴봐도..<br>여잘 만나도 왜 조금도 나아지지 않는거야?<br>어떡해야 낫는 거야?<br>자꾸 눈물만 흘리는 병이..<br>한사람만 생각나 가슴 아픈 병이..<br>어떻게 널 잊겠어?<br>더이상 난 못하겠어....<br>제발 돌아와 웃으며 얘기해줘....거짓말이었다고,다....<br>', 'N', '황세준', '', '', 'https://docs.google.com/uc?export=open&id=1c6Cx66xYbUpAygmPwAa1DCpIdxrIDHoi');
