insert into music(mseq, abseq, atseq, theme, chart, gseq, title, content, titleyn, musicby, lyricsby, producingby, src) values(music_seq.nextval, 73, 17, '', '1|2', 1, 'Your Lights', '나는 흙으로부터 왔고<br>다시금 그리 가겠지<br>한낱 그런 삶일 뿐인데 <br>그게 다인데<br>새벽은 짙고 나는 깊어 <br>잠겨버렸네<br><br>사랑의 모양과 사람의 진심을<br>무엇도 알지 못한 채 <br><br>세상의 모든 일에 <br>해답을 찾는 것은<br>무거운 어깨 위로 <br>짐을 얹게 되는 것<br>마음 놓을 자리를 <br>쉽게 찾지 못하고<br>고단한 삶 속에서 <br>모두 버려지는 내 꿈들<br><br>이 새벽이 끝나길 바라<br>너와 나 둘이서 가자 빛으로<br>결국 떠나길 잘 했다고 <br>웃는 두 눈은<br>어둠이 걷힌 새 아침을 <br>보게 될 거야<br><br>사랑의 모양과 사람의 마음도<br>조금은 알 수 있다고<br><br>그렇게 믿는 일이 <br>서로를 지키는 것<br>선명한 모습으로 <br>너의 앞에 서는 것<br>어두운 질문 속에도 <br>환한 답을 찾는 것<br>우리를 위한 길이 <br>거기 있다고 믿어 나는<br><br>이 길을 달려가자 <br>새벽을 걷어내고<br>사랑을 하자 미움을 져버리고<br>여기 이대로 그저 곁에 있을게<br>우리가 갈 수 없는 길은 <br>없다고 말해 줄게', 'Y', '타이비언, 김정우 (TOXIC)', '린 (LYn)', '타이비언, 김정우 (TOXIC)', 'https://docs.google.com/uc?export=open&id=1RrL-wYWT4-XbzBe5gC7R8JNUjyD8Szum');
insert into music(mseq, abseq, atseq, theme, chart, gseq, title, content, titleyn, musicby, lyricsby, producingby, src) values(music_seq.nextval, 74, 18, '', '1|2', 1, '이미 나에게로', '이꿈이 멎어버린 순간에 난 깨닫고<br>널 생각해 어쩜 난 너에 힘겨움을<br>함께 짊어지고 갈수 있어<br>할말이 많기도 하고<br>목소리도 그리워 전화를 했어<br>씁쓸한 목소리가 들리고<br>난 미안함에 당황도 했어<br>난 너무 힘들어서 그런 이유로<br>만나기 미안해 둘이 할 때는<br>너만을 생각하려 했는데<br>문득 어떤 고민에<br>널 앞에 두고 뭐가 그리 힘들어<br>내 미래에 완성속에 있는 너인데~<br>이미 나에게로하여 집착하게 만든 넌 방황에 지쳐<br>쓰러져 버린 내가 다시 일어서는<br>모습 생각하며 날 찾을수 있니<br>이미 나에게로하여 집착하게 만든 넌<br>실패에 주저앉은 처참한 날 본 후<br>다시 용기 얻는 모습 생각하며<br>날 사랑해 줄수 있겠니<br>낙엽이 진 나무위로 하얀 서리가 내린 새벽 내가<br>이세상을 보았을 때 얌전하고<br>고요하게 내곁에 있어준 너를 생각하며<br>', 'Y', '', '', '', 'https://docs.google.com/uc?export=open&id=1x-TQf1Xkb2y4LFzUxMSv88Q9o-m9jTQf');
insert into music(mseq, abseq, atseq, theme, chart, gseq, title, content, titleyn, musicby, lyricsby, producingby, src) values(music_seq.nextval, 74, 18, '', '1|2', 1, '기억할수 밖에 없는 너', '뒤돌아서 앞만 보고가<br>네눈에서 멀어질 테니까<br>항상 너의 그림자만<br>쫓아다녔었지<br>이제 나의 기억속에서 너를 지울거야<br>널 사랑해야 했던 많은 이유들이<br>처음부터 끝날 때까지 바뀌지 않았었어<br>넌 날 슬퍼하게 만든<br>이유가 됐기에<br>이제는 나도 보내주고 싶어<br>기억에 남겨둔 너의 모습까지<br>슬픈 꿈을 꾼 것뿐야<br>깨어나면 다시<br>사랑이 시작된 날로 돌아갈지도 몰라<br>기억할 수 밖에 없는 너였었지만<br>이제는 나도 보내주고 싶어<br>기억에 남겨둔 너의 모습까지<br>이제 아주 가끔 생각속에<br>볼 수 있음을 난 만족할 뿐<br>어찌됐든 너와 나는<br>각기 가진 의식속을 침범할 수 아니하지 못하겠지<br>그래 그래 이제 예<br>뒷모습에 눈물만큼 아쉬움도 남아 있나<br>예고없는 네 생각에 피해본건 나뿐이잖아<br>니가 내게 보인만큼<br>니가 나를 사랑했나 아니 내가 너를 사랑했지<br>뒤돌아서 앞만 보고가<br>네 눈에서 멀어질 테니까<br>항상 너의 그림자가 되고 싶었지만<br>이제 나를 원치않는<br>너는 필요없어<br>널 잊으려고 했던 많은 이유들은<br>나를 보는 너의 눈빛이<br>바뀔 때부터였지<br>난 널 초라한 손길로<br>붙잡진 않겠어<br>이제는 나도 보내주고 싶어<br>기억에 남겨둔 너의 모습까지<br>슬픈 꿈을 꾼 것뿐야<br>깨어나면 다시<br>사랑이 시작된 날로 돌아갈지도 몰라<br>기억할 수 밖에 없는 너였었지만<br>이제는 나도 보내주고 싶어<br>기억에 남겨둔 너의 모습까지<br>널 사랑해야 했던 많은 이유들이<br>처음부터 끝날 때까지 바뀌지 않았었어<br>넌 날 슬퍼하게 만든<br>이유가 됐기에<br>이제는 나도 보내주고 싶어<br>기억에 남겨둔 너의 모습까지<br>', 'N', '', '', '', 'https://docs.google.com/uc?export=open&id=13ok15SuuJ1RIABXeo0UmVq3f7-Pf6032');
insert into music(mseq, abseq, atseq, theme, chart, gseq, title, content, titleyn, musicby, lyricsby, producingby, src) values(music_seq.nextval, 74, 18, '', '1|2', 1, '너를 만나게한 바다로', '1. 너는 새벽 안개가 빚어놓은 해맑은 이슬을 맞은듯<br>촉촉히 젖은 긴머리 그 위로 엷은 햇살이 흐르네<br>우리 처음 만난 그날 일들이 네게 기억되어지고 있을까<br>내겐 멀미처럼 느껴진 그날에 기억을 난 잊지못해<br>먼 수평선 끝에 하늘이 노을속에 지고있던 그날 그바다<br>너를 만나 삶에 풋풋함을 배웠던 그바다로 오늘은 가겠어<br>너를 만나러 바다로<br>(Naration)저기 기울어가는 노을 속에 이세상 아픔을 잠재<br>우고<br>하늘을 비상하는 새처럼 이젠 자유롭게 날을 수 있어<br>너를 만나기 전에 나는 모든게 미완의 단계였었어<br>하지만 지금의 나는 무엇이든 자신있어 너만 있으면...<br>2. 햇살 가득한 아침 붉어져가는 그대의 뺨위에 살며시<br>나의 입술을 파도에 담아 보내고 싶었어<br>', 'N', '', '', '', 'https://docs.google.com/uc?export=open&id=1vPHhn4Vv8en0bVXq5IY1BY2H4950zFSO');
insert into music(mseq, abseq, atseq, theme, chart, gseq, title, content, titleyn, musicby, lyricsby, producingby, src) values(music_seq.nextval, 74, 18, '', '1|2', 1, 'J에게', 'J! 스치는 바람에 J! 그대 모습 보이면<br>J! 오늘도 조용히 그댈 그리워 하네<br>J! 지난밤 꿈속에 J! 만났던 모습은<br>내 가슴 속싶이 여울져 남아 있네<br>J! 아름다운 여름날이 멀리 살졌다 해도<br>J! 나의 사랑은 아직도 변함 없는데<br>j! 난 너를 못잊어 J!나 너를 사랑해<br>J! 우리가 걸었던 J! 추억의 그길을<br>난 이밤도 쓸쓸히 걷고 있네.<br>', 'N', '', '', '', 'https://docs.google.com/uc?export=open&id=1RCQbxeNcACx92Ti1jFPkvBdCu88LC3RX');
insert into music(mseq, abseq, atseq, theme, chart, gseq, title, content, titleyn, musicby, lyricsby, producingby, src) values(music_seq.nextval, 74, 18, '', '1|2', 1, 'Ya Ya! Boo Boo! (너를 알고부터)', '1.아무것도 몰랐었어 YA...... 지금의 너를 알기까지 YA......<br>아무말도 지금은 필요 하지 않아 난 단지 너를 알고부터 모든 것들이<br>너에게 어울리게 변했던거야. 우....<br>나를 구속 하려 하지마 내 마음은 모두 너의거야 잡으려고 노력하지마<br>너는 벌써 나를 붙잡았어 이제는 초조한건 나야 아무말 하지마<br>하루라도 너를 잊을 수 있다는건 불가능<br>할뿐이야 괜한 걱정이야<br>2,너도 나를 사랑하지BOO...서로가 서로 지켜주지BOO.... 너 말고는<br>내 자신 흔들리지 않아 오늘도 너를 보고있어 어떤 상대도 나에게<br>어울리지 않을 뿐이야 우.... 너무 잘 보이려 하지마 다른 사람들의<br>눈에 뛰어 남을 의식하려 하지마 그러다간 너의 실수가돼<br>술취해 비틀거린 나를 외면하지 않고 밤을 새워 내 곁에서 지켜<br>주는건 바로 너 너뿐이야 나의 사랑이야<br>', 'N', '', '', '', 'https://docs.google.com/uc?export=open&id=1nSJVvR6faIzwO2hCWCUCCdA7g9WxchHF');
insert into music(mseq, abseq, atseq, theme, chart, gseq, title, content, titleyn, musicby, lyricsby, producingby, src) values(music_seq.nextval, 74, 18, '', '1|2', 1, '거짓 같은 진실', '받아서 채워지는 그런 사랑 보다는 너에게<br>주기만 하는 그런 아름다운 사랑으로<br>널 내게 드러놓고 싶어<br>거짓이라 말하지마 서툰 나의 사랑은 단지<br>네게 표현하려고 했을 뿐야 오해가 됐다면 사과할께<br>이 세상에서 너뿐인건 거짓이 아닌 진실인걸<br>너에게 했던 어색한 변명도<br>널 위해 서라면 난 다시 할 수 있어(우.....)<br>난 이제는 실수하지는 않아<br>너의 친구들도 부러워 할만큼<br>난 모든 걸 할 수 있다는 걸 너에게 보여주고 싶어<br>그런 슬픈표정 짓지마 넌 내게있어 소중해<br>이제는 나에게 웃음만 보여줘<br>이 세상에서 너뿐인건 거짓이 아닌 진실인걸<br>너에게 했던 초라한 약속도<br>지키지 못한 건 지금도 미안해<br>', 'N', '', '', '', 'https://docs.google.com/uc?export=open&id=1WnwcNW3rZXeO_YuIq94draBt8aPdGz4s');
insert into music(mseq, abseq, atseq, theme, chart, gseq, title, content, titleyn, musicby, lyricsby, producingby, src) values(music_seq.nextval, 74, 18, '', '1|2', 1, '블루 파티', '우째 이런 일이! 화려한 이 파티에<br>친구들은 저마다 웃고 마시면서 조잘대며<br>잘들 노는데 하필이면 어쩌다 내가만든<br>이좋은 파티에 주인공은 혼자서 외로워야만<br>하나 무너지는 모습에 희미한 불빛만이<br>나를 위로하네 (참담함 표정으로)<br>결국 너없는 파티에서 속없이 난 웃고있어<br>나의 시선은 항상 저편 출입문에<br>다가있어 한참 동안을 바라보다 친구들의<br>웃음속에 잠시 아찔한 현기증을 난 느꼈어<br>보이지 않은 네 모습을 찾으려고 서성이다<br>문득 마주친 라이벌의 조롱섞인 한마디가<br>나를 비참히 만든거야 오지 않는 너 때문에<br>정말 너만을 위한 파틴데 막이 내린 파티에<br>친구들은 다 떠나고 슬픈 그림자만 남겨진<br>블루파티야<br>블루파티가 돼버렸지 내가 있어도<br>너는 없어 넌 꼭 오리라 생각했어<br>결국 너는 날 속인거야 파티고 뭐고<br>소용없어 니가 없이는 의미없지<br>너는 약속만 어겼지만 나의 마음은 무너졌어<br>', 'N', '', '', '', 'https://docs.google.com/uc?export=open&id=13WABlN20C-AUKFPzKT4zAGCT-lCqs8C4');
insert into music(mseq, abseq, atseq, theme, chart, gseq, title, content, titleyn, musicby, lyricsby, producingby, src) values(music_seq.nextval, 74, 18, '', '1|2', 1, '그후로 나는', '한동안 너를 잊어야 했어<br>눈물이 마르도록 잊었던 거야<br>(talking) No one help me when I am crying<br>No one help me when I am down<br>oh! I''m lost in s dark<br>그 후로 나는 슬픔에 빠졌어<br>친한 친구에게도 말을 못했어<br>나의 일기에 눈물로 번진<br>Good bye라는 말만 난 쓸 수<br>밖엔 없었던거야<br>이제 난 웃는 날 보다는<br>슬픈 날이 내게 남았어<br>난 너를 바라만 볼거야<br>아주 오래된 나의 노트에<br>담겨져 있는 눈물 같은<br>추억을 맞으며<br>사랑은 가까이 있는데 넌 무엇을 찾아 떠났니<br>너에게 이별을 말하지<br>않았지만 우리에 사랑은<br>끝나기 전에 모든 게 끝났던 거야<br>', 'N', '', '', '', 'https://docs.google.com/uc?export=open&id=1Uoi-U3NccAti6vRs1nFJkrQf7A3wAGHh');
insert into music(mseq, abseq, atseq, theme, chart, gseq, title, content, titleyn, musicby, lyricsby, producingby, src) values(music_seq.nextval, 75, 18, '', '1|2', 1, '혼자만의 이별', '난 오늘도 이렇게 한참을 걸어서<br>이 길까지 왔어<br>그대가 사는 곳이라는 그 설레임으로<br>막연한 기대를 안고서<br>그대 닫혀진 문앞을 지나쳐갈때면<br>자꾸 돌아보지<br>누군가 날 부르는 소리<br>그대일까 하지만 그건 바람이었어<br>거칠게 살아왔던 나의 모습으로<br>그대 앞에 당당히 나설 수가 없어<br>소용없는 짓이라고 날 탓해보지만<br>변함없는 미련에 난 어쩔 수 없어<br>그대여 너무 모자란 내게<br>기회를 주지마<br>자신할 수 없는 내 미래에 불행해져<br>나 지금 떠나야겠어<br>그대의 행복을 바래<br>익숙해진 이 길을 떠나네<br>', 'Y', '', '', '', 'https://docs.google.com/uc?export=open&id=1rm6MZrFa1UG-wYNZPtY_j3uoiOl3vrvP');
insert into music(mseq, abseq, atseq, theme, chart, gseq, title, content, titleyn, musicby, lyricsby, producingby, src) values(music_seq.nextval, 75, 18, '', '1|2', 1, '절교 선언', '넌 너만의 욕심으로 멀쩡한 두남자를 아주 우습게 만들었어<br>어떻게 널 생각해야 되는건지<br>난 도대체 이해하기 힘이들 어 무슨 말좀해<br>넌 아무렇지 않은 듯<br>언제나 자극적인 웃음으로 날 반겼는 데<br>감쪽같이 이 남자 저 남자 사이로 오가는 넌 이게뭐야<br>이게 니취미 생활이니<br>끝내야겠어 속은 이느낌 결혼까지 생각한 내가 경솔했어<br>사랑이라고 난 착각한거야 이제보니 넌 별로 이쁘지 않아<br>오오 눈물을 보이지마 모두가 연극이야<br>그녀석 앞에서도 넌그럴 테지<br>끝까지 니식대로 날 그냥 비웃어도 그래야만 이충격을 난 빨리 잊을거야<br>널 내 여자란 이유로 모두가 부러워할 만큼<br>잘해주었었는데 너 는 그저 그상황을 즐긴거고<br>친구들은 껍질뿐인 너와나를 축 복한거야<br>끝내야겠어 속은 이느낌 결혼까지 생각한 내가 경솔했어<br>사랑이라고 난 착각한거야<br>이제보니 넌 별로 이쁘지 않아<br>오오 눈물을 보이지마<br>모두가 연극이야 그녀석 앞에서도 넌그럴 테지<br>끝까지 니식대로 날 그냥 비웃어도<br>그래야만 이충격을 난 빨리 잊을거야<br>너에게 화를내야<br>정상일 듯한데 왠지 니가 불쌍하게 보여<br>너를 진정 사랑할 사람이 있을까 그취미를 바꾸기전엔<br>세상 모든 남자가 모두 다 네것같이 지맘대로 된다는 그상 상을 깨 내가 널 사랑한건 그래도 순수했어<br>언젠가 날 기억 하면 너는후회할거야<br>눈물을 보이지마 모두가 연극이야<br>그녀석 앞에서도 넌 그럴 테지<br>끝까지 니식대로 날 그냥 비웃어도 그래야만 이충격을 난 빨리 잊을거야<br>세상 모든 남자가 모두 다 네것같이 지맘대로 된다는 그상 상을 깨<br>내가 널 사랑한건 그래도 순수했어 언젠가 날 기억 하면 너는후회할거야  예<br>', 'N', '', '', '', 'https://docs.google.com/uc?export=open&id=1ev8WjoXS3B5Xc-OOpCbUszLYAe5jNAPh');
insert into music(mseq, abseq, atseq, theme, chart, gseq, title, content, titleyn, musicby, lyricsby, producingby, src) values(music_seq.nextval, 75, 18, '', '1|2', 1, '소녀에게', '날 잃었었어 나를 둘러싼<br>내 삶의 원에서<br>하지만 내가 꿈꿔왔던 소녀를 만났지<br>그 소녀는 내가 잃었던<br>나를 지키고 있으니<br>네게 주어질 어떤 고통도<br>내 맘이 함께 받고 싶어<br>난 포기하지 않을거야<br>결코 난 너만 내게 있어주면<br>몇번이고 기나긴 저 능선을<br>내달릴 수 있어<br>늘 내곁에 머물러 있을 수 있게<br>난 너의 생각이 되어<br>이세상이 아름다워지도록<br>너와 같이 보고 느낀 것을<br>나 말하겠어<br>난 소녀에게 약속해<br>가끔 이 세상의 아픔속에<br>헤매어도 나 널 보면서<br>이겨가겠어<br>모든 정성 다 바쳐<br>내 꿈과 미래를<br>소녀를 위해서 만들어내겠어<br>훗날 인생의 끝되어 우리 달리해도<br>또다시 나 그녀와 사랑하기 위해서<br>날 잃었었어 나를 둘러싼<br>내 삶의 원에서<br>하지만 내가 꿈꿔왔던 소녀를 만났지<br>그 소녀는 내가 잃었던<br>나를 지키고 있으니<br>네게 주어질 어떤 고통도<br>내 맘이 받겠어<br>너만 내곁에 있어 준다면<br>너만 내곁에 있어 준다면<br>', 'N', '', '', '', 'https://docs.google.com/uc?export=open&id=1xxAyrXb42Jw2MZTOFZhZNc-2-dZZXly5');
insert into music(mseq, abseq, atseq, theme, chart, gseq, title, content, titleyn, musicby, lyricsby, producingby, src) values(music_seq.nextval, 75, 18, '', '1|2', 1, '갈증', '1.이세상은 이미 너무 타락했고 나는 그저 세상에 둘든것이라고 내 스스로 자기 최<br>면을 걸어 나의 허튼 행돌을 변명해 왔어 어지러운 거리의 불빛을 모두 꺼버릴듯<br>비가 오는데 다가오는 이 갈증은 뭘까 나의 가슴속의 외침이 들려 힘든 세상살이<br>에 밀려나 이리저리 표류하던 내 영혼 이제 욕망의 허물을 깨뜨리고 나의 정직한<br>알몸을 찾는다 우우 세상은 어둡지 않아<br>2.언제나 제먹대로 행동하고 조그마한 실수도 인정히 않아 그 모든건 내탓이 아닌거<br>야 사람들이 망쳐논 세상탓이지 골치아픈 세상애길 왜해 표정없는 얼굴들이 더 많<br>아 나에게 상관없는 일들은 나도 그렇게 무시하며 살았어 아무도 필요없단 고집에<br>나를 건드리면 화를 냈었지 하지만 이제 누군가가 필요해 나？ 외로움을 쓰다듬어<br>줘<br>Rap 내가존재하는 이공간은 세상가운데 어디쯤인가 여긴 어디야 어딘데 내자유를 이<br>토록 멍들게 하는지 누구좀 말해줘 모든것을 알아버리기전으로 돌아가 순수했던 내<br>모슴을 찾고 싶어 나의 가슴속에 불타는 욕수 또 더 깊은 곳에 베여있는 내 가엽슨<br>모습이 공존하는걸 깨달았을땐 더이상 여기에 존재하고 싶진 않아<br>', 'N', '', '', '', 'https://docs.google.com/uc?export=open&id=1mE2kK3pQUNuqrxux_BAeSKcEyDRXVAEr');
insert into music(mseq, abseq, atseq, theme, chart, gseq, title, content, titleyn, musicby, lyricsby, producingby, src) values(music_seq.nextval, 75, 18, '', '1|2', 1, '승자의 제안', '힘들던 과거를 웃으며 떠올릴 수 있다면 그건 삶의 승리로<br>누려지는 행복의 여유지 좌절의 연속이다라고만 느낀다면<br>어떤이의 미소를 보며 질투하지 말길 바래 차곡차곡 쌓은<br>우리의 공든탑을 시기하는 저들의 눈에 존경의 눈물이 흐르<br>는 것을<br>맛보려면 예! 벅차다라는 말을 실감할 수 있을 때<br>우리앞에 놓인 비통함을 추스릴 수 있어야해<br>지금을 아깝게 흘려버리는 나, 내밖에서 그 모습을 볼 수<br>있었더라면<br>여지없이 그런날 욕할 텐데 왜 나에게서 난 벗어나지 못하<br>는가<br>커다란 너의 모습을 그리면서 또 잠이들어 버리면 그게뭐야<br>환한 너의 내일과 그 많은 꿈의 댓가로 너의 많은 인내와<br>노력을<br>치뤄야해 끝없이 노력하며 난 살아갈거야 유혹에 지지 않고<br>날 채찍하며 모든 걸 이룬후 뒤를 보았을 때 힘들던 과거가<br>내게 박수를 보낼테니 우리에겐 몰랐었던 특별함이 있어<br>그것을 찾았을 땐 자 용기를 내 모든 걸 이룬후 뒤를 보았<br>을때 힘들던 과거가 홀가분할테니<br>(Chorus)내곁에 너의 승리의 그림자를 보며 외면했던 우리<br>의 현실을<br>알아야해 내곁에 너의 승리의 그림자를 보며 외면했던 우리<br>의 현실을<br>찾아야해 내곁에 너의 승리의 그림자를 보며 외면했던 우리<br>의 현실을<br>알아야해 우리만의 인내와 노력을 찾아야해<br>', 'N', '', '', '', 'https://docs.google.com/uc?export=open&id=1-Q1i7DoTOk1FpvHc7bUQSsgQokEREh7a');
insert into music(mseq, abseq, atseq, theme, chart, gseq, title, content, titleyn, musicby, lyricsby, producingby, src) values(music_seq.nextval, 75, 18, '', '1|2', 1, '내 안에 있는 너', '처음부터 내가 아닌 날<br>보라며 숨겨왔었지<br>서로 다른 생활 속에서<br>커가는 차이 느낄 때마다<br>변해가는 너의 모습을 바라만 볼 수 밖에<br>돌이킬 수 없는 후회<br>속으로 날 내 버려둔 채<br>선명했던 내 안에 너를<br>느끼며 지내왔었지<br>서로 다른 바램 속에서<br>조그만 실망 쌓일 때마다<br>변해가는 너의 모습을 바라만 볼 수 밖에<br>돌이킬 수 없는 후회 속으로<br>날 내 버려둔 채<br>넌 지금 내게 없어도 항상<br>내 곁에 맴도는 너의 향기<br>그 속에서 널 그리며 얼마나<br>많은 시간을 보내야만 하나<br>', 'N', '', '', '', 'https://docs.google.com/uc?export=open&id=1XCqEWA83PvMCNXpcwOkJSDhs2gOeXe85');
